const aws = jest.genMockFromModule('aws-sdk');

class DocumentClient {
  scan(params) {
    return {
      promise: () => {
        return new Promise((resolve, reject) => {
          if (params.resolve) {
            return resolve(params.resolve);
          }
          if (params.reject) {
            return reject(params.reject);
          }
          reject('Test Failed');
        });
      }
    };
  }
}

aws.DynamoDB.DocumentClient = DocumentClient;

module.exports = aws;
