const SUCCESS = 'SUCCESS';
const FAILED = 'FAILED';

class ResponseGenerator {
  static getError({
    code = 500,
    errorMessage = 'No Error Message Available',
    status = FAILED
  } = {}) {
    return {
      code,
      status,
      errors: [
        {
          message: errorMessage
        }
      ]
    };
  }

  static getResponse({ code = 200, payload = {}, status = SUCCESS } = {}) {
    return {
      code,
      status,
      payload
    };
  }
}

module.exports = ResponseGenerator;
